alias team-repos-do='team_repos_log="$(mktemp)"; for repo in $(readlink $HOME/team-repos/*); do pushd $repo >> $team_repos_log; basename $repo; b=$(git branch --show-current); stashed=""; [[ "$(git stash list | wc -l)" = "$(git stash >> $team_repos_log && git stash list | wc -l)" ]] || stashed=yes; git switch master 2>1 >> $team_repos_log; '
alias team-repos-done='git switch @{-1} 2>1 >> $team_repos_log; [[ -n "$stashed" ]] && git stash pop >> $team_repos_log; popd >> $team_repos_log; done'
